input.Binds = {}function input.Bind(key, cmd)	check(key, "string")	check(cmd, "string", "nil")	luadata.SetKeyValueInFile("%DATA%/input.txt", key, cmd)	input.Binds[key] = cmdendfunction input.Initialize()	input.Binds = luadata.ReadFile("%DATA%/input.txt")endfunction input.Call(key, press)	if input.DisableFocus then return end	press = press and "" or "~"	local cmd = input.Binds[press .. key]	if cmd then		console.RunString(cmd)		return false	endendevent.AddListener("OnKeyInput", "keybind", input.Call, print, math.huge)function input.Command(line, key, ...)	if key then		cmd = table.concat({...}, " ")		input.Bind(key, cmd)	endendconsole.AddCommand("bind", input.Command)